float getInd(in vec2 v){
  v.x = mod(v.x, 2.0);
  v.y = mod(v.y, 2.0);
  if (v.x == 1.0 && v.y == 0.0) return 0.0;
  if (v.x == 0.0 && v.y == 1.0) return 1.0;
  if (v.x == 1.0 && v.y == 1.0) return 2.0;
  return -1.0;
}

vec3 hsb2rgb(float h, float s, float b){
  vec3 c = vec3(h, s, b);
  vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
  rgb = rgb * rgb * (3.0 - 2.0 * rgb);
  return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord*2.0 - iResolution.xy)/min(iResolution.x, iResolution.y);

  uv *= 6.0;
  float t = fract(iTime/2.0);
  float q = (1.0+sqrt(5.0))/2.0;
  float a = pow(q,t*2.0);
  float b = pow(q-1.0,t*2.0);
  vec2 va = vec2((1.0/sqrt(5.0))*(a*q + (q-1.0)*b), (1.0/sqrt(5.0))*(a-b));
  vec2 vb = vec2((1.0/sqrt(5.0))*(a-b), (1.0/sqrt(5.0))*(a*(q-1.0)+b*q));
  float det = va.x*vb.y-va.y*vb.x;
  vec2 w = (1.0/det) * vec2(vb.y * uv.x - vb.x * uv.y, -va.y * uv.x + va.x * uv.y);

  vec2 wi = floor(w);
  vec2 target = vec2(0.0);
  float d = 9999.0;
  // targetは最近格子点、dはそれとの距離
  for(float i=-1.0; i<=1.0; i += 1.0){
    for(float k=-1.0; k<=1.0; k += 1.0){
      vec2 targetTmp = vec2(wi.x + i, wi.y + k);
      vec2 p = targetTmp.x * va + targetTmp.y * vb;
      float dTmp = length(p - uv);
      if (dTmp < d) {
      d = dTmp;
      target = targetTmp;
      }
    }
  }

  vec2 p = target.x * va + target.y * vb;
  uv -= p;
  float angle = iTime * 6.28318 * 0.25;
  uv *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
  float luminosity = 0.1/(abs(uv.x) + abs(uv.y));

  vec3 baseColor = vec3(1.0);
  float offset = mod(floor(iTime/2.0), 3.0);
  float ind = getInd(target);
  if (ind > -0.5) {
    ind += 3.0 - offset;
    ind = mod(floor(ind), 3.0);
    if (ind == 0.0) baseColor = hsb2rgb(0.4, 1.0, 0.7);
    if (ind == 1.0) baseColor = hsb2rgb(0.5, 1.0, 1.0);
    if (ind == 2.0) baseColor = hsb2rgb(0.65, 0.7, 1.0);
  }

  baseColor *= luminosity;

  fragColor = vec4(baseColor, 1.0);
}
