void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize coordinates
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Colors
    vec3 pumpkinColor = vec3(1.0, 0.5, 0.1);
    vec3 stemColor = vec3(0.3, 0.2, 0.1);
    vec3 backgroundColor = vec3(0.0, 0.0, 0.2);

    //  pumpkin shape
    float pumpkinRadius = 0.7;
    float pumpkinShape = length(uv) - pumpkinRadius + 0.1 * sin(uv.y * 10.0);  // Adds slight horizontal ridges
    vec3 color = mix(backgroundColor, pumpkinColor, smoothstep(0.05, 0.0, pumpkinShape));

    //  stem
    if (uv.y > 0.5 && abs(uv.x) < 0.1) {
        color = mix(color, stemColor, smoothstep(0.1, 0.05, abs(uv.x)));
    }

    // face (eyes, nose, and mouth)
    float eyeSize = 0.1;
    if (length(uv - vec2(-0.2, 0.1)) < eyeSize || length(uv - vec2(0.2, 0.1)) < eyeSize) {
        color = backgroundColor;  // Left and right eyes
    }

    float noseSize = 0.05;
    if (length(uv - vec2(0.0, -0.1)) < noseSize) {
        color = backgroundColor;  // Nose
    }

    float mouthWidth = 0.4;
    float mouthHeight = 0.1;
    if (abs(uv.y + 0.3) < mouthHeight && abs(uv.x) < mouthWidth * (0.5 + 0.5 * cos(uv.x * 15.0))) {
        color = backgroundColor;  // Mouth
    }

    // Output color
    fragColor = vec4(color, 1.0);
}
