float Circle(vec2 uv, vec2 p, float r, float blur){

    float d = length(uv-p);
    float c = smoothstep(r, r-blur,d);
    return c;
}



float Smiley(vec2 uv, vec2 pos, float size){
    uv /=size;
    uv -=pos; //translate coordinate system


    float mask = Circle(uv, vec2(0.), 0.4, 0.05);

    mask -= Circle(uv, vec2(.15,0.05),.1,.01);
    mask -= Circle(uv, vec2(-.15,0.05),.1,.01);

    float mouth=Circle(uv, vec2(0.,-.1), 0.15, 0.001);
    mouth-=Circle(uv, vec2(0., -.01), 0.20, 0.01);

    mask -=clamp(mouth, 0., 1.);   
    return mask;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv -=.5;
    uv.x *= iResolution.x/ iResolution.y;

    vec3 color = vec3(0.);
    float smile=Smiley(uv, vec2(0.), 1.);

    //color = vec3(mouth);
    color += vec3(1.0, 1., .0)*smile;

    fragColor = vec4(color,1.0);
}
