void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = 1.-uv.y;
    uv *= 128.;
    uv.x = float(int(uv.x));
    uv.y = float(int(uv.y));
    uv /= 128.;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(.3);

    if (abs(uv.x) < 1.) {
        vec3 ro = vec3(cos(iTime), 0, 0);
        vec3 rd = normalize(vec3(uv, 1.));

        for (int d = 0; d < 10; d++) {
            ro += rd;
            if (length(ro - vec3(1., sin(iTime), 3.)) < .3) {
                col = vec3(.8);
                break;
            }
            if (length(ro - vec3(0, -sin(iTime), 3.)) < .3) {
                col = vec3(171./256., 82./256., 54./256.);
                break;
            }
            if (ro.y > 1.) {
                if (mod(ro.x+iTime, .3) < 0.15)
                    col = vec3(1., 0, 0);
                else
                    col = vec3(1., 1., 1.);
                break;
            }
        }
    }

    fragColor = vec4(col,1.0);
}
