float deltaTime(float offset) 
{
    return sin(iTime + offset) * 0.25;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = fragCoord/iResolution.xy; // <0,1>
  float time = sin(iTime);
  float amount = iResolution.x; 

  vec3 col = vec3(0); // start with black
  float i = float(fragCoord.x);
  float decimal = i/(amount);
  if (uv.y > (deltaTime(decimal*(iTime*iTime/((iResolution.x - i) * 1.))) + 0.45) && 
      uv.y < (deltaTime(decimal*(iTime*iTime/((iResolution.x - i) * 1.))) +.55)){
      col = 0.5 + 0.5*cos(iTime+uv.yyy+vec3(0,2,4)); 
  }



  // Output to screen
  fragColor = vec4(col,1.0);
}
