vec4 boring_crap(vec2 uv, int i) {
    float f = float(i);
    vec3 col = 0.5 + 0.5*cos(iTime+f*vec3(2,3,4)+uv.xyx+vec3(0,2,4));
    float alpha = -0.4 - cos(uv.x * 20.0);
    float fw = fwidth(alpha);
    alpha = smoothstep(-fw, fw, alpha);
    col *= 1.0 - f * 0.03;
    return vec4(col, 1) * alpha;
}

vec2 futz(vec2 uv) {
    vec2 cs = cos(0.5 + vec2(0.0,-0.5)*3.1415926);
    uv += sin(uv.yx * 5.3 + iTime) * 0.08;
    uv *= mat2x2(cs.x, -cs.y, cs.y, cs.x);
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    for (int i = 0; i < 2; ++i) {
        uv = futz(uv);
        uv.yx = futz(uv.yx);
    }

    vec4 op = vec4(0);
    for (int i = 0; i < 10; ++i) {
        vec4 colour = boring_crap(uv, i);
        op = mix(colour, op, op.a);
        if (op.a > 0.98) break;
        uv = futz(uv.yx);
    }

    fragColor = op;
}
