vec2 complex_pow(vec2 a) {    
    return vec2(a.x * a.x - a.y * a.y, 2. * a.x * a.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {  
    float zoom = 2.5;    
    if (mod(iTime, 34.) <= 17.) {
        zoom = 2.5 / pow(2., mod(iTime, 17.));   
    } else {
        zoom = 0.000007 * pow(2., mod(iTime, 17.));    
    }
    vec2 uv = fragCoord/iResolution.xy * zoom - 0.5 * zoom;    uv.x -= 0.77;
    uv.y += 0.0998;    
    vec2 zn = uv;
    float abs_zn = 0.;    vec3 col = vec3(0.);
    for (int i = 0; i < 10000; ++i) {       
        zn = complex_pow(zn) + uv;
        abs_zn = zn.x * zn.x + zn.y * zn.y;       
        if (abs_zn > 4.) {
            col = vec3(min(float(i) / 1000., 1.), min(float(i) / 100., 1.), 1. / abs_zn * float(i)); 
            break;
        }    
    }
    fragColor = vec4(col, 1.0);
}
