float smin(float a, float b, float k) {
    float h = max(k - abs(a-b), 0.) / k;
    return min(a, b) - h*h*h*k*1./6.;
}

float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normal shit I know
    vec2 uv = fragCoord/iResolution.xy;   
    uv-=0.5;
    uv*=2.0;
    uv.x *= iResolution.x/iResolution.y;
    uv.x += 0.3;
    uv.y += 0.4;
    // end of normal shit I know


    // This is like a resolution, the more you increase the number (2 here) the 
    // blurrier it gets and if it's one it's like it's not anti-aliased
    float pix = 2./iResolution.y; 

    // This creates the distance for the 2 circles using the circle sdf
    // and changes the position of the second one with a sin time so fancy
    float d1 = sdCircle(uv, 0.3);
    uv.x -= 0.6 + sin(iTime /3.0);
    float d2 = sdCircle(uv,0.2);


    // This makes the bg white?? I'm not sure why
    vec3 col = vec3(1.0);
    // The first line mixes the colour with black and a smooth step using the aformentioned
    // resolution and has the smooth minimum of the two distances with k and a 
    // value that determines the thickness of the border, same with the second line 
    // but that flips the resolution and makes it so it's less thickness.
    col = mix(col, vec3(0.), smoothstep(-pix,pix, smin(d1,d2,0.8) - .0375 ));
    col = mix(col, vec3(0.), smoothstep(pix,-pix, smin(d1,d2,0.4) - .0065 ));
    // This makes the outer border red because fancier
    col *= vec3(1.0,0.0,0.0);


    fragColor = vec4(col,1.0);


}
