void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;



    //sphere shapes
    float r[3] = float[3](
        0.5,
        0.5,
        0.8
    );
    vec3 center[3] = vec3[3](
        vec3(0.5,0.5,0.5),
        vec3(0.5,0.2,0.5),
        vec3(-0.5,-0.5,0.5)
    );


    //ray
    vec3 org = vec3(0,0,-1.0f);
    vec3 dir = vec3(p,1);
    dir = normalize(dir);


    vec3 DebugColor_OrbOnPath = vec3(0);
    float E = 0.200001;
    //ray tracing (collision test)
    for(int i = 0;i<3;i++){
        if(abs(dot(normalize(center[i]-org),normalize(dir)))>1.0f-E){
            DebugColor_OrbOnPath = vec3(abs(dot(normalize(center[i]-org),normalize(dir))));  
        }
    }




	fragColor = vec4(DebugColor_OrbOnPath,1.0);
}
