void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float frag_size = 32.;
    vec2 uv = fract(fragCoord / frag_size);
    vec2 S = sin(fragCoord / 20. + iTime) + 1.;
    uv = vec2(step( 0.5 * S.x * S.y, length(uv - 0.5)));

    // Output to screen
    fragColor = vec4(uv.x);
}
