//                                               fragCoord：输入一个像素坐标
//                                                   |
//                                                   |
//                       fragColor：输出该像素颜色      
//                           |                       |
//                           |                       |
void mainImage( out vec4 fragColor,      in vec2 fragCoord )
{
    //vec4 BackColor = vec4(0.50); // 灰色背景

    if(floor(fragCoord.y)==floor(iResolution.y*0.5)){ // 中心水平线（一像素宽）                                        

         fragColor = vec4(0.50 - 0.10); // 暗10%   

    }else if(floor(fragCoord.x)==floor(iResolution.x*0.5)  ){// 中央竖线（一像素宽）         

         fragColor = vec4(0.50 + 0.10); // 亮10%

    }else{// 其它区域

         fragColor = vec4(0.50); // BackColor

    }
}












































