void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord / iResolution.xy ; // a√±adimos * 2.0 -1.0 para hacer un lenght 


   uv -= .5;
   uv.x *= iResolution.x / iResolution.y;
   vec3 col = vec3 (0.0);

   float d = length(uv);
   //float c = d;
   float b = 0.3;


   float h = smoothstep(b ,b-0.1 , d); 

   //if(d <.3) c = 1.; else c = 0.0;

   col = vec3(1.0, 1.0, 0.0) * h; // multiplicamos color por "shape o textura"
   fragColor = vec4(col,1);


   //fragColor = vec4(uv.x, uv.x, uv.x, 1.0);


}
