#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
#define time iTime
#define resolution iResolution.xy
#define size 75.

vec2 Hash12(float t)
{
    float x = fract(sin(t * 36.3) * 453.2);
    float y = fract(sin(t * 54.3) * 453.2);

    return vec2(x,y);
}
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);


    vec2 position = 2.0 * ( C.xy - 0.5*resolution.xy) / resolution.y;
     vec2 uv = (C-.5 * iResolution.xy)/iResolution.y;
	  float tt = iTime*0.0000000001;
	//m.x = m.x * resolution.x / resolution.y;
    vec3 col2;
     uv*=mat2(cos(iTime*0.055),1,-1 ,sin(iTime*0.055));
    for(float i = 0.; i < size; i++)
    {
        uv*=mat2(cos(iTime*0.015),sin(iTime*0.015),-sin(iTime*0.015) ,cos(iTime*0.015));

        vec2 dir= Hash12(i+tt) -.5; 

        float d = length(uv-dir);

        float brightness = 0.00012*i+0.00001;
        col2 += vec3(brightness*0.05 /d);


    }   
	for(float i = 0.; i < 55.; i++)
    {
     vec2 dir= Hash12(i+tt) -.5; 

       dir*=mat2(cos(iTime),sin(iTime),-sin(iTime) ,cos(iTime));
        float d2 = length(uv-dir);

        float brightness = 0.012+i*0.0001;
	vec2 m = 2.0*(vec2(0.01, 0.5) - 0.5);
	float a = 0.02 / length(position-m);
	float b = 0.01 / length(position-m);
	float c = 0.001 / length(dir-m);

	float d = sin(10.0*10.0 + position.x*position.y);

   vec3 col = vec3(brightness*0.6 /d2);
	O+= 0.035 * + vec4(a+col.x, b, c+col.x, 1.0);
    }
    vec3 p,r=iResolution,
    d4=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(
        float i=0.,g=0.,e,s;
        ++i<99.;
        O.rgb+=mix(r/r,H(log(s)),.7)*.08*exp(-i*i*e))
    {
        p=g*d4; 

        vec2 c = Hash12(p.z);
        p.xy*=c;
       p.xy*=mat2(1.,1,1 ,1);
        p.z-=.6;
        p=R(p,normalize(vec3(1,2,3)),iTime*.0);
        s=4.;

        for(int j=0;j++<8;)


            p=abs(p),p=p.x<p.y?p.zxy:p.zyx,
            s*=e=1.8/min(dot(p,p),1.3),
            p=p*e-vec3(12,3,3);
        g+=e=length(p.xz)/s;

    }
    O=pow(O,vec4(5));
 }
