void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float radius = min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord.xy / vec2(radius, radius);

    float dist = distance(vec2(0.9, 0.5), uv);
    vec4 r = vec4(smoothstep(0.5, 0.51, 1.0 - dist), 0.0, 0.0, 0.0);
    vec4 g = vec4(0.0, smoothstep(0.5, 0.51, 1.0 - dist), 0.0, 0.0);
    vec4 b = vec4(0.0, 0.0, smoothstep(0.5, 0.51, 1.0 - dist), 0.0);
    vec4 rings = b;
    fragColor = rings;
}
