void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 1.3*sin(iTime+uv.xyx+vec3(4,2,9)) - 0.5*sin(iTime+uv.xyx+vec3(1,2,4)) + 0.3*cos(iTime/(vec3(1,2,6)*iResolution.xxy*(iTime/3.14159)));

    vec4 color = vec4(col,1.0)+floor(uv.xxyy+col.xyzz+sin(iTime))+refract(sin(iTime+uv.xyyx+vec4(4,2,9,1)),sin(iTime+uv.xyxx+vec4(1,2,4,1)),abs(atan(iTime/0.0211)));
    fragColor = color + refract(vec4(mix(color.x,color.y,color.z),mix(color.x,color.y,color.z),mix(color.x,color.y,color.z),mix(color.x,color.y,color.z)),vec4(0.71619,0.71619,0.71619,0.71619),0.5168) + 1.0/2.7182;
