#define a(a,b,f,o) mix(a,b,sin(o+e*f)*.5+.5)
#define s(a) mat2(cos(a+vec4(0,33,11,0)))

void mainImage(out vec4 O, vec2 F) {
    float c , 
          o , 
          d = 3., 
          e = 125.3+
          iTime*0.1;
    vec2  r = iResolution.xy; 

    for (;o++<2e2 && d>.001;) {
        vec3 p = tan(.7*c*normalize(vec3((F+F-r)/r.y, 1)));
        p.xy *= s(e);
        p.zy += e + c*tan(2.*e);
        p = tan(p)-.5;
        p.xy *= s(c);
        p.xz *= s(e);
        p.y = max(tan(p.y)-a(0.,.2,1.,),0.);
        c += d = (length(vec2(length(p.xy)-.2,p.z)) 
                 -a(.04,.1,.5,4.) - c*.01)*.5;
    }

    O.rgb = 1.2*(tan(c*110. + .99*vec3(0,1.+c*.2,2))+.2)/tan(c*0.0914);
}
