float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 fill_color = vec3(0.0,1.0,1.0); // 圆的填充色
    vec3 background_color = vec3(0.5,0.5,0.8);//背景色
    float radius = 40.0; // 圆的半径
    vec2 center = iMouse.xy;  // 圆的中心点

    // 获取屏幕上的某一点到圆中心的向量
    vec2 fragToCenter = fragCoord - center;

    // 计算SDF
    float sdfValue = sdCircle(fragToCenter,radius);

    // 计算更新之后的颜色
    background_color = mix(background_color,fill_color,1.0 - smoothstep(0.0, 0.01, sdfValue));
    fragColor = vec4(background_color, 1.0);

}
