const vec2 center = vec2(400, 220);
const vec3 bkg = vec3(0.);
const vec3 gradient = vec3(0., 0., 1.);
const vec3 edge = vec3(0.678, 0.847, 0.902);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 pixel = uv * iResolution.xy;
    float radius = 200.;
    float d = distance(pixel, center);

    vec3 col = vec3(1.);

    if (d < radius) {
        col = mix(bkg, gradient, (d-100.)*0.009);
    }

    fragColor = vec4(col,1.0);
}
