// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-07-13 22:04:27

#define sx 10.
#define sy 10.
void mainImage(out vec4 O,vec2 F)
{
    F*=vec2(sy,sx)/iResolution.xy;
    F-=.5;
    F.x+=.25;
    float d = 2.;
    for (float n;n<sx;n++)
    {
        for (float m;m<sy;m++)
        {
            float l = length(F-vec2(m+mod(n,2.)*.5,n));
            if (l<d)
            {
                d=l;
                O.rg=vec2(m+mod(n,2.)*.5,n);
            }
        }
    }
    //O=vec4(d);
    // uncomment the line above an comment the rest below for the distance.
    O.rg=normalize(O.rg-F);
    if (dot(O.rg,vec2(0,-1))>.5) O=vec4(1);
    else if (dot(O.rg,vec2(-.866,.5))>.5) O=vec4(.5);
    else O=vec4(.8);
}
