// very much inspired by kishimisu's works, check them out !!

void mainImage( out vec4 o, in vec2 F )
{
    vec2 R = iResolution.xy,
    u=(F+F-R.xy)/R.y;
    //u*=1.5; 
    float i, // i loop
    t=iTime; // time
    vec3 col=vec3(0.);
    for(o*=i;i++<32.; // loop
        o+=vec4(vec3(
            .005/abs(length(vec2( // main circle sdf
            u.x+(i/32.+sin(t*i/8.)/2.-.5), // modulate x movement
            u.y+(i/32.+cos(t*i/8.)/2.-.5)))-.07)) // modulate y movement
            *vec3(vec3(.8,.3,.2) + vec3(.7,.5,1.)*cos(6.*(vec3(.6,.3,.3)*(t/8.+(i/16.))+vec3(2.8,3.,-.3)))), 0.)); // colorization
}
