#define h11(a) fract(sin(a * 453.2734) * 994.3434)

float objs(vec2 p) {
    vec2 w = vec2(3.14 / 13.);
    float t, id, fw, h = 22.,
          l = length(p) / dot(p,p);
          //l = 2. - length(p); // fun

    p.x = atan(p.y, p.x);
    id = floor(p.x / w.x);
    t = h11(id) + iTime * .4;
    fw = fract(t) * w.y;
    p.y = log(l) + fw + sin(id + t) * .3;
    if(p.y < .0) h = 1.;
    p.y +=(h / 2. - .5) * fw;
    w.y *= h;
    p = mod(p, w) - w / 2.;
    return l * (length(p) - .11);
}

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
    u = vec2(u + u - r) / r.y;
    o = vec4(smoothstep(3. / r.y, .0, objs(u)));
}
