float pi=3.1415926;

vec2 LRCooridnate(in vec2 p,in float Num,in float dis)
{


    vec2 SaUV=(-iResolution.xy+2.0*p)/min(iResolution.x,iResolution.y);
    vec2 SaUVC=vec2(-SaUV.x,-SaUV.y);

    float VTRV=1.-fract(atan(SaUVC.x,SaUVC.y)/pi/2.+0.25);
    float V2=(ceil(VTRV*ceil(Num))-0.5)/Num*pi*2.;
    float V3s=sin(V2)*dis;
    float V3c=cos(V2)*dis;
    vec2 V4=vec2(V3c,V3s)+SaUVC;

    float V11=ceil(VTRV*ceil(Num))*2.-1.;
    float V105=360./(ceil(Num)*2.)*(-1.);

    float cor=-V11*V105;
    float th=(2.*pi)/360.*cor;
    float rx=V4.x;
    float ry=V4.y;
    vec2 V5r=vec2(rx*cos(th)+ry*sin(th),ry*cos(th)-rx*sin(th));

    return vec2(V5r);
}

float StarSDF(in vec2 LRCooridnate,in float SCD,in float Scale)
{
    float SCD1=-(90.-SCD/-2.)*((2.*pi)/360.);

    float thl=SCD1;
    float rx=LRCooridnate.x;
    float ry=LRCooridnate.y;
    vec2 V5L=vec2(rx*cos(thl)+ry*sin(thl),ry*cos(thl)-rx*sin(thl));

    float SCDL=V5L.x*(0.5/Scale)*(1./(cos(SCD1)*0.5))*step(LRCooridnate.y,0.);

    float ly=-LRCooridnate.y;
    vec2 V5R=vec2(rx*cos(thl)+ly*sin(thl),ly*cos(thl)-rx*sin(thl));
    float SCDR=V5R.x*(0.5/Scale)*(1./(cos(SCD1)*0.5))*step(-LRCooridnate.y,0.);

    return -(SCDL+SCDR);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 FUV=LRCooridnate(fragCoord.xy,5.,1.);
    float RFUV=StarSDF(FUV.xy,50.,1.);

    fragColor=vec4(RFUV,RFUV,RFUV,1.);
}
