// reference:https://www.shadertoy.com/view/llsGW7



// i copy this from FabriceNeyret2's comment
// i'm trying to make it more like a chinese painting

vec3 colorSun = vec3(1.0,0.45,0.35);
float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage( out vec4 c, vec2 w )
{
    vec2 uv = w/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec4 layer3 = vec4(colorSun, 1.0-(smoothstep(0.0,iTime*0.0002+0.25,sdCircle( vec2(uv.x+0.6,uv.y-0.4), 0.01 ))));
    vec4 p = vec4(w/iResolution.xy,1.1,1)-.5, d=p,t;
    vec4 layer1 = vec4(0.);
     vec4 layer2 = vec4(0.);
    p.z += iTime*1.5; d.y-=.2;
    p *= 2.;
    for ( float s,i=600. ; --i>0. ; p+=1.2*d ) {
        for( t-=t, s = .5; s < 5.; s+=s ) 
            t += texture( iChannel0, .25+ p.xz*s/3e3 )/s;
        layer1 = (0.4+d.z-t*i/1e3),
        t.x > p.y*.018+2.6 ? i-=i : i;
        c =  mix(layer1, layer3, layer3.a);
    }
    //vec3 col =  vec3 (step(0.0,sdCircle(uv, 0.1 )));
    //c = vec4(col,1.0);


}
