// FabriceNeyret2: 262 char
// and then I chopped 2 chars for 260
void mainImage( out vec4 c, vec2 u )
{
    float i;  
    vec2 R = iResolution.xy;
    u = 2.* ( u+u - R ) / R.y;

    for (c *= i ; i++< 10.; )
        c += min(1., .01/abs(1.-length(u))) * .05, // rings
        u *= 1.618 * mat2(cos( i*iTime*.31416 + vec4(0, 11, 33, 0))), // scale & rotate
        u.x = abs(u.x) - 1.272, // shift
        c += .02/length(u) + .01/length( abs(u)-vec2(0, 1) ); // points

    c += c*c;
}

