#define res (64.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec2 f = fract(uv*res);
    //f = 1.0 - f;
    uv = floor(uv*res)/res;



    vec4 col00 = texture(iChannel0, uv + vec2(0.0, 0.0) / res);
    vec4 col10 = texture(iChannel0, uv + vec2(1.0, 0.0) / res);
    vec4 col01 = texture(iChannel0, uv + vec2(0.0, 1.0) / res);
    vec4 col11 = texture(iChannel0, uv + vec2(1.0, 1.0) / res);
    f = 1.0 - f;
    f = smoothstep(0.0, 1.0, f);
    f = sin(f*3.14156*3.);
    vec4 col = 
    (col00 * f.x + col10 * (1.0 - f.x)) * f.y + 
    (col01 * f.x + col11 * (1.0 - f.x)) * (1.0 - f.y);

    // Output to screen
    fragColor = col;
}
