vec3 palette( in float t)
{
    vec3 a = vec3(0.948, 0.558, 0.948);
    vec3 b = vec3(1.318, 0.876, 0.656);
    vec3 c = vec3(1.298, 0.498, 0.209);
    vec3 d = vec3(0.536, 0.476, -1.834);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    float tile_size = ((sin(iTime)/4.0)+(sin(0.3*iTime)/2.0))/2.0 + 1.1;

    for (float i = 0.0; i < 4.0; i++) {

        uv = fract(uv * tile_size) - 0.5 ;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(-length(uv0) + iTime*0.5 + i*0.4);

        d = sin(d * 8.0 - iTime/2.0) / 8.0;
        d = abs(d);

        d = pow(0.002 / d, 1.9);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
