#define MZ(z) mat2(z, -z.y, z)
#define GONEXT if ( !limithit )                             \
                  for (float i = -1.; i < 2.; i += 2.) {    \
                      l++;                                  \
                      vec2 z = (z+vec2(i,0)) * cinv;        \
                      if ( length(z) > r ) limithit = true; \
                        else               maxl = max(l, maxl); 

#define ENDNEXT       limithit = false; l --;               \
                  }

struct complex {
    float re;
    float im;
};

float squareabs(complex c) {
    return c.re*c.re+c.im*c.im;
}

complex mult(complex c1, complex c2) {
    complex c3;
    c3.re = c1.re * c2.re - c1.im * c2.im;
    c3.im = c1.re * c2.im + c1.im * c2.re;
    return c3;
}



vec3 mandelbrot(vec2 z, vec2 c) {
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if(dot(z, z) > 65536.0) break;
    }
    l = l - log2(log2(dot(z,z))) + 4.0;
    return vec3(l, z);
}




vec3 littlewood2(vec2 z, vec2 c) {
    vec2 origz = z;
    int l,maxl;
    float c2 = dot(c,c), r = 1./(1.-length(c));
    if (c2 > 1.)  return vec3(0., origz);
    mat2 cinv = MZ(c) / c2;
    bool limithit = false;
    GONEXT
        GONEXT
            GONEXT
                GONEXT
                    GONEXT
                        GONEXT
                            GONEXT
                                GONEXT
                                    GONEXT
                                    ENDNEXT
                                ENDNEXT
                            ENDNEXT
                        ENDNEXT
                    ENDNEXT
                ENDNEXT
            ENDNEXT
        ENDNEXT
    ENDNEXT
    float maxl_float = float(maxl);
    return vec3(maxl_float / 10.5, origz);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = ((fragCoord) / iResolution.y);
    float res = 24.0;
    vec2 puv = floor(uv * res) / res;
    float c2 = dot(puv,puv);
    if (c2 > 1.) {
        puv = vec2(-puv.y, puv.x) / c2;
    }
    float scale = 2./(1.-length(puv));
    vec2 muv = mod(uv, 1.0 / res) * res * scale - (scale / 2.0);

    vec3 f = littlewood2(muv, puv);
    float l = f.x;
    vec2 z = f.yz;
    fragColor = vec4(vec3(l) * vec3(0.4, 0.7, 1.), 1.0);
    //fragColor = vec4(ref);
}
