#define ScreenSize iResolution

vec4 cartoonTransition(vec2 fragCoord, float t) {
    float blur = 10./ScreenSize.y;
    vec2 uv = (fragCoord - 0.5*ScreenSize.xy) / length(ScreenSize.xy*0.5);
    float dist = length(uv);
    float progress = (dist - (1.0 - (1.0+blur)*t));
    float result = smoothstep(0.0, blur, progress);
    return vec4(vec3(0.0), result);
}

float zigzag(float t, float pauseLow, float pauseHigh, out float id) {
    float m = 2.0 + pauseLow + pauseHigh;
    id = floor((t - 1.0 - 0.5*pauseHigh)/m);
    return clamp(abs(mod(t+pauseLow*0.5+m*0.5, m)-m*0.5)-pauseLow*0.5, 0.0, 1.0);
}

float hash11(float p) {
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= 2.0*p;
    return fract(p);
}

vec3 hueToColor(float hue)
{
    // Regular hsv with s, v = 1
    return clamp(abs(fract(hue + vec3(3, 2, 1)/3.0) * 6.0 - 3.0) - 1.0, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Time varying pixel color
    float id;
    float t = zigzag(iTime, 5.0, 1.0, id);
    vec3 tint = hueToColor(hash11(id));
    vec4 transitionColor = cartoonTransition(fragCoord, t);
    vec3 col = mix(texture(iChannel0, uv).rgb*tint, transitionColor.rgb, transitionColor.a);

    // Output to screen
    fragColor = vec4(col,1.0);
}
