// 蓝光光环着色器  
void mainImage( out vec4 fragColor, in vec2 fragCoord )  
{  
    // 将像素坐标归一化（从0到1）  
    vec2 uv = fragCoord / iResolution.xy;  

    // 将坐标系统中心化  
    uv = uv * 2.0 - 1.0; // 将坐标范围从[-1, 1]转换  
    uv.x *= iResolution.x / iResolution.y; // 根据屏幕宽高比调整x坐标  

    // 计算与中心的距离  
    float dist = length(uv); // 计算从中心到当前点的距离  

    // 创建基础光环  
    float ring = smoothstep(0.5, 0.4, dist) * smoothstep(0.3, 0.4, dist);   
    // 使用平滑过渡函数生成光环的形状  

    // 为光环增加一些变化  
    float ring2 = 0.1 * sin(atan(uv.y, uv.x) * 10.0 + iTime * 2.0) + ring;   
    ring2 *=ring;
    // 利用角度和时间增加光环的波动效果  

    // 创建颜色渐变  
    vec3 col =   vec3(0.1, 0.4, 0.8) * ring2;   
    // 基础颜色 + 根据ring变化的渐变颜色  

    // 增加一些动态效果  
    float angle = iTime; // 你可以根据需要调整这个角度  


    // 使用旋转后的坐标进行计算  
    col += 0.25 * sin(iTime + uv.x * 10.0) * sin(iTime + uv.y * 10.0) * ring;  
    // 在颜色中加入基于时间的动态变化  

    // 输出到屏幕  
    fragColor = vec4(col, 1.0); // 生成最终的颜色输出，透明度为1.0  
}
