#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(2,3,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*e;
    p.z-=2.;
    //p.xz*=R2D(t*.3);

    for(j=0.;j++<4.;)
        p=(abs(R3D(t*.5))-.08),
        d=min(min(p.z,p.x),p.y),
        d-=dot(p,q)/1e4;

    e+=d;
}o+=vec4(exp(p/d/i),1)*3./i;
}
