// https://www.shadertoy.com/view/dsKczW neve
// https://www.shadertoy.com/view/ddKcRD titanic

#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime * .75

void mainImage(out vec4 o, vec2 u) {    
    vec2 r = iResolution.xy; 
         u = (u - r.xy / 2.) / r.y;

    o = vec4(0);
    vec3 D = normalize(vec3(u, 2)), f,
         p = vec3(sin(t * .2) * 11., cos(t) * .2, t * 3.1),
         k = p + vec3(cos(.07 * t), sin(.08 * t) * .3, sin(3.* t) * .4 + 4.);

    D.zy *= rot(sin(t) * .08);
    D.zx *= rot(cos(t * .1) * .1);

    float i, j, e, d = 1., 
          nuvem, nivelDagua, 
          ball, teto, chao;

    while(i++ < 120. && d > .01){

        ball = length(p - k) - .2;
        nuvem = abs(p.y -  1.1) - .02;
        chao = p.y + .4;    
        nivelDagua = p.y - .8;

        j = 1.;
        e = 0.;
        while(j < 512.)
            j += j,
            f = p * j * 1.,
            f.y -= t * j * 1.8 + t*3., 
            f.yz *= rot(j),
            e += abs(dot(sin(f), vec3(1))) / j - .03;

        d = min(
                ball,
                min(
                    nuvem, 
                    min(
                        chao, 
                        max(.0, nivelDagua)
                    )
                ) + e * .09 
            );

        p += d * D;
    }

    j = 2.;
    o += j / (length(p - k) - .1 + j);

    if(d > nivelDagua)
        o.r *= (length(o.rg) * 1.5 + 2.) * .5;
        o.bgr -= cos(t * 6.) * .1;

    o = o*o*o;
}
