// Procedural Wood Test By Rik Riesmeijer, 2024 - No rights reserved.
void mainImage(out vec4 c, in vec2 z) {
    z = 7.0 * z / iResolution.xy - 3.0 + vec2(0.3, 1.4) * sin(iTime);
    vec2 o = vec2(2.3 * cos(iTime) - 1.4, sin(iTime));
    z += (vec2(z.x, -z.y) + o) / dot(z, z);
    z += vec2(z.x, -z.y) / dot(z, z) + 7.0;
    c = vec4(0.3, 0.1, 0, 1);
    c.rg += 0.4 * fract(fract(length(z)) / fract(dot(z, z)));
}
