float targetDelta = 1.0;

vec3 blueColor = vec3(65.0/255.0, 60.0/255.0, 201.0/255.0);
vec3 yellowColor = vec3(252.0/255.0,168.0/255.0,3.0/255.0);


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 source = vec2(0.5, 0.5);

    float angle = atan(uv.x - source.x, uv.y - source.y);
    angle += 3.1415; 

    float factor = 1.0 / angle;

    factor = pow(factor,.6);
    factor = smoothstep(0.1,0.9,factor);

    blueColor *= factor + 0.2;
    vec3 final = mix(blueColor,yellowColor,factor);
   //final = yellowColor * factor;
   fragColor = vec4(final,1.0);
}
