void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-99998.9*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);


    float m = .0;
    float t = iTime*.5;
    float dist = length(uv);

        vec2 gv = fract( uv*.1*(60000.) ) + sin(uv);


    for (float y=-1.; y<=100.; y++){
        for (float x=-1.; x<=1.; x++){
        vec2 offs = vec2(sin(iTime), y);

        float d = length(gv-offs);

        float r = mix(.025, 3., sin(t+dist)*.5+.5);
        m += smoothstep(r, r/7., d);

    }
    }


   // col.rb = gv;
    col += mod(m, 2.);

    fragColor = vec4(col,1.0);
}
