float sdEquilateralTriangle( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}



float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdRhombus( vec3 p, float la, float lb, float h, float ra )
{
  p = abs(p);
  vec2 b = vec2(la,lb);
  float f = clamp( (ndot(b,b-2.0*p.xz))/dot(b,b), -1.0, 1.0 );
  vec2 q = vec2(length(p.xz-0.5*b*vec2(1.0-f,1.0+f))*sign(p.x*b.y+p.z*b.x-b.x*b.y)-ra, p.y-h);
  return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}



vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);

    p=rotate_y(p, radians(270.0));

     p.y=p.y+10.0;
    vec3 p0=p;
    //float planeDist1 = p.y+10.0;  //piso inf
    //res =opU2(res, vec2(planeDist1,101.0));

    p.y=p.y-5.0;

    float d1=sdSphere(p-vec3(0.0,0.0,0.0) , 10.0 );
    float d2=sdSphere(p-vec3(0.0,0.0,0.0) , 9.0 );
    vec2 pn= Rotate(p.xy-vec2(9.0,3.0), vec2(0.0,0.0), radians(iTime));

    float d3= sdEquilateralTriangle(pn, 5.0 );

    float df= Sdf_D(d1, d3);

    res =opU2(res, vec2(df,100.0));
    res =opU2(res, vec2(d2,24.0));

    float la,lb,h,ra ;
    la=1.75; lb=0.5, h=0.5; ra=0.2;

    vec3 pn2=p.yxz-vec3(0.0,8.5,-5.0);
    vec3 pn3=p.yxz-vec3(-1.0,10.0,-1.0);
    vec3 pn4=p.yxz-vec3(-1.0,10.0, 2.0);
    vec3 pn5=p.yxz-vec3(0.0,8.5, 5.0);

    float d1a= sdRhombus( pn2,la,lb, h,ra );
    float d2a= sdRhombus( pn3,la,lb, h,ra );
    float d3a= sdRhombus( pn4,la,lb, h,ra );
    float d4a= sdRhombus( pn5,la,lb, h,ra );

    ///pestaña 2
    p.y=p.y-6.;
     pn2=p.yxz-vec3(-1.0,7.0,-5.0);
     pn3=p.yxz-vec3(0.0,8.5,-1.0);
     pn4=p.yxz-vec3(0.0,8.5, 2.0);
     pn5=p.yxz-vec3(-1.0,7.0, 5.0);

    float d1b= sdRhombus( pn2,la,lb, h,ra );
    float d2b= sdRhombus( pn3,la,lb, h,ra );
    float d3b= sdRhombus( pn4,la,lb, h,ra );
    float d4b= sdRhombus( pn5,la,lb, h,ra );


    res =opU2(res, vec2(d1a,101.0));
    res =opU2(res, vec2(d2a,101.0));
    res =opU2(res, vec2(d3a,101.0));
    res =opU2(res, vec2(d4a,101.0));

    res =opU2(res, vec2(d1b,101.0));
    res =opU2(res, vec2(d2b,101.0));
    res =opU2(res, vec2(d3b,101.0));
    res =opU2(res, vec2(d4b,101.0));


    ///ojo
    p=p0;
    float ps=3.0*sin(iTime);
    float d1c= sdCylinderYZ( p-vec3(0.0,7.0,0.0+ps), vec2(2.0,10.0) );
    float d2c= sdCylinderYZ( p-vec3(0.0,7.0,0.0+ps), vec2(1.0,10.0) );
    d1c=Sdf_I(d2,d1c);
    d2c=Sdf_I(d2,d2c);

    res =opU2(res, vec2(d1c,5.0));
    res =opU2(res, vec2(d2c,0.0));

    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
    for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }    
    return object;
}


float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 9999.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}


vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos = p;
     vec3 V = normalize(ro - p);
    vec3 N = normal;
    vec3 L = normalize (lightPos - worldPos);
    vec3 R = reflect (-L, N);
    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);
    float shadow = getSoftShadow(worldPos, L);// shadows
    float occ = occlusion(worldPos, N);// occ
    vec3 ambient = Ka + Ka * dot(normal, vec3(0., 1., 0.))*lightColor;
    ambient*=0.5;
    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff= clamp(dot(N, L), 0.0, 1.0);
    vec3 diffuse =  lightColor * diff;
    float shininess=10.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);
    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0); // back
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.25 +back) + vec3(.5)*specular*specular;
    return colOut;
}



