void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord / iResolution.xy;  //Normalize resolution

    st -= 0.5;  // Center the coordinate system

    st.x *= iResolution.x / iResolution.y;  // Correct for aspect ratio

    vec2 pos = st;

    vec3 col = vec3(0.0);

    float r = length(pos) * 3.0;
    float a = atan(pos.y, pos.x);   

    float sinValue = sin(iTime*2.0);
    float scaleFactor = mix(2.0, 20.0, (sinValue + 1.0) / 2.0);
    float f = cos(a * scaleFactor);
    // f = abs(cos(a*3.));
    // f = abs(cos(a*2.5))*.5+.3;
    // f = abs(cos(a*12.)*sin(a*3.))*.8+.1;
    // f = smoothstep(-.5,1., cos(a*10.))*0.2+0.5;

    col = vec3(1.0 - smoothstep(f, f + 0.02, r));

    fragColor = vec4(col, 1.0);
}
