void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    vec2 st = gl_FragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;


    vec3 color = vec3(1.000,0.749,0.185);

    // Shadow
    if( distance(vec2(0.5,0.48),st) < 0.3 ){
        color = vec3(0.920,0.635,0.052);
    }

    // Circle
    if( distance(vec2(0.5),st) < 0.3 ){
        color = vec3(0.015,0.490,0.500);
    }

    // inside shadow
    if( distance(vec2(0.5),st) < 0.14 ){
        color = vec3(0.920,0.635,0.052);
    }

    // inside circle
    if( distance(vec2(0.5,0.49),st) < 0.14 ){
        color = vec3(1.000,0.749,0.185);
    }

    // left eye
    if( distance(vec2(0.4,0.78),st) < 0.03 ){
        color = vec3(0.015,0.490,0.500);
    }
    if( distance(vec2(0.4,0.78),st) < 0.02 ){
        if( st.y-0.76 < abs(sin(iTime*1.5))* 0.15){
        	color = vec3(0.035,0.026,0.006);    
        }
    }

    // right eye
    if( distance(vec2(0.6,0.78),st) < 0.03 ){
        color = vec3(0.015,0.490,0.500);
    }
    if( distance(vec2(0.6,0.78),st) < 0.02 ){
        if( st.y-0.76 < abs(sin(iTime*1.5))* 0.15){
        	color = vec3(0.035,0.026,0.006);    
        }
    }

    // Happy Mouth
    if( distance(vec2(0.5,0.73-0.01),st) < 0.04 ){
        if(st.y-0.73 < 0.0){
            color = vec3(0.035,0.026,0.006);
        }
    }
    if( distance(vec2(0.5,0.68-0.01),st) < 0.035 ){
        if(st.y-0.68 > 0.0){
            if(distance(vec2(0.5,0.73-0.01),st) < 0.04){
                color = vec3(0.945,0.194,0.140);
            }
        }
    }


    fragColor = vec4(color,1.0);


    // Output to screen
    //fragColor = vec4(col,1.0);
}
