float time(){
    return iDate.x+iDate.y+iDate.z+iDate.w;
}

float mouse_s(){
    return iMouse.x+iMouse.y+iMouse.z+iMouse.w+float(iFrame)/1.0;
}


vec3 hash(vec3 t)
{
    uvec3 x=uvec3(t);
    const uint k = 1103515245U;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

void mainImage(out vec4 rgba, in vec2 crd){
    vec2 xy=crd;
    vec2 nxy=xy/iResolution.xy;
    vec3 color = vec3(0,0,0);



    for(float i=2.0, mx=max(iResolution.x,iResolution.y); i<=mx; i*=2.0){
        vec2 loc_coord=floor(xy/iResolution.xy*i);
        vec2 normed=loc_coord/i*mx*iResolution.xy;
        float time_norm=log2(mx/i);
        vec3 loc_color=vec3(hash(vec3(normed,floor((time()+mouse_s())/time_norm)*time_norm)));

        //for(float x=-1.0; x<=1.0; x+=1.0)
        //    for(float y=-1.0; y<=1.0; y+=1.0){
        //        vec2 crd=vec2(loc_coord.x+x,loc_coord.y+y);
        //        normed=crd/i*iResolution.xy;
        //        loc_color+=vec3(hash(vec3(normed,time()+mouse_s())));
        //    }
        float k1=1.0/max(i,mx/i)*10.0;
        float k2=1.0/log2(mx);
        color+=loc_color*k2;
    }


    rgba = vec4(color,1.0);
}
