#define R vec2(1,0)

vec2 cmul(vec2 z, vec2 c) {
    return vec2(z.x * c.x - z.y * c.y, z.x * c.y + z.y * c.x);
}

vec2 cdiv(vec2 z, vec2 c) {
    float r = dot(c, c);
    return vec2(z.x * c.x + z.y * c.y, z.y * c.x - z.x * c.y) / r;
}

vec2 conj(vec2 z) {
    return vec2(z.x, -z.y);
}

vec2 hypershift(vec2 z, vec2 s) {
    return cdiv(z + s, cmul(z,conj(s))+vec2(1.0,0.0));
}

vec2 recip(vec2 z) {
    return conj(z)/dot(z,z);
}

vec3 cf(float a, vec3 base) {
    return 0.5+0.5*sin(a+base);
}

vec4 fC(vec2 fragCoord)
{
    vec2 uv = 2.0 * (fragCoord.xy - 0.5*iResolution.xy) / -iResolution.y;

    float t = iTime * 0.25;

    vec2 z = uv;

    z = hypershift(z, vec2(0.5,-0.5));
    z = cmul(z,vec2(cos(t),sin(t)));

    z = dot(z,z)>1.?recip(z):z;

    z *= 8.;
    for(int i = 0; i < 50; i++) {
        z += 0.05*cos(z.yx);
    }

    vec2 c = uv;
    c = hypershift(c, vec2(-0.5,0.5));
    c = cmul(c,vec2(cos(-t),sin(-t)));
    for(int j = 0; j < 8; j++) {
        c = cdiv(c-R,c+R);
        c += recip(c);
    }
    c *= 4.;

    float dz = cos(mod(length(z),1.)+mod(abs(z.x)*abs(z.y),abs(z.x)));
    float dc = sin((length(c)*abs(c.y))*asinh(abs(c.x)))+abs(c.y)*abs(c.x);

    vec3 col = dz>dc?cf(dz,vec3(0.5,1.5,2.5)+iTime):cf(dc,vec3(iTime*0.1));

    return vec4(tan(col+0.1),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    float A = 2.,
          s = 1./A, x, y;

    for (x=-.5; x<.5; x+=s) for (y=-.5; y<.5; y+=s) fragColor += min ( fC(vec2(x,y)+fragCoord), 1.0);

	fragColor /= A*A;
}
