#define pi 3.14159265
void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv = (2.*iMouse.xy-r)/r.y;

    vec3 a = vec3(.1, .2, .9);
    vec3 b = vec3(.6, .4, .6);
    vec3 c = vec3(.2, .6, .2);
    vec3 d = vec3(.6, .5, .3);
    vec3 e = sin(iTime + vec3(0, 2, 4))*.3 + .5;//vec3(.4);
    vec3 f = vec3(.1, .4, .5);

    vec3 col = mix(
        uv.y > .8?a:uv.y > .6?f:uv.y > .4?c:uv.y > .2?f:e, 
        uv.y > .8?b:uv.y > .6?b:uv.y > .4?f:uv.y > .2?d:a, 
    uv.x);

    if (uv.y > .8){
        col = sin(uv.x*pi*2. + vec3(2.*pi/3., 4.*pi/3., 0))*.5 + .5;
    }

    // Y = 0.299 R + 0.587 G + 0.114 B - permanently borrowed from the hitchiker's guide to digital colour
    float y = dot(col, vec3(.299,.587,.114));

    if (fract(iTime/3.) < .7){
        col /= y;
        col *= .4;
    }


    col = pow(col, vec3(1./2.2));
    O = vec4(col, 1.);
}
