#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime

void mainImage(out vec4 o, vec2 u) {    
    vec2 r = iResolution.xy; 
         u = (u - r.xy / 2.) / r.y;

    o -= o;
    vec3 D = normalize(vec3(u, 2)), f, q, 
         p = vec3(cos(t * .2), sin(t * .3) * .5, -7);
    float x, i, j, d = 1., s;

    while(i++ < 120. && d > .004){
        q = p;

        d = length(q * .9) - .5;
        x = length(q * .8) - 1.2;
        d *= .09;

        j = 1.;
        while(j < 12.)
            f = p * j * 3., 
            f.xz *= rot(j + t * .25),
            d += abs(dot(sin(f), vec3(1))) / j * -.04,
            j += j;

        s = d;
        d = abs(d) - .008;

        s < .008
            ? o += (vec4(2, 3, 6, 0) * .002)
            : o += (vec4(3, 3, 3, 0) * .0015);

        d = max(d * 2., -x) + .006;
        p += d * D;
    }

    o = 1. - o;
    o = o * o * o;
}
