float Circle(vec2 uv, vec2 p, float r, float blur) {
    float d = length (uv - p);
    float c = smoothstep(r, r - blur, d);

    return c;
}

float Smiley(vec2 uv, vec2 p, float size) {
    uv -= p;
    uv /= size;

    float mask = Circle(uv, vec2(0.0), 0.4, 0.01);

    mask -= Circle(uv, vec2(-0.13, 0.2), 0.07, 0.01);
    mask -= Circle(uv, vec2(0.13, 0.2), 0.07, 0.01);

    float mouth = Circle(uv, vec2(0.0, 0.0), 0.3, 0.02);
    mouth -= Circle(uv, vec2(0.0, 0.1), 0.3, 0.02);

    mask -= mouth;

    return mask;
}

float Band(float t, float start, float end, float blur) {
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep(end + blur, end - blur, t);


    return step1 * step2;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur) {

    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);


    return band1 * band2;

}

float Remap01(float a, float b, float t) {
    return (t - a) / (b - a);

}

float Remap(float a, float b, float c, float d, float t) {

    return Remap01(a, b, t) * (d - c) + c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);

    float mask = 0.0;

    float x = uv.x;
    float m = sin(x * 16.0) * 0.1;
    float y = uv.y + (m * sin(iTime));

    float blur = Remap(-1.0, 0.7, 0.01, 0.25, x);
    blur *= blur;

    mask = Rect(vec2(x, y), -1.0, 0.7, -0.2, 0.2, blur);

    float smiley = Smiley(uv, vec2(0.0), 1.0);

    col = vec3(1.0, 1.0, 1.0) * mask * smiley;

    fragColor = vec4(col,1.0);
}
