void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m =iMouse.xy;
    if (iMouse.x<1.0&&iMouse.y<1.0) m=iResolution.xy/2.0;
    vec2 coord = fragCoord-iResolution.xy*1.5+m.xy*2.0;
    float x = coord.x;
    float y = coord.y;
    float t = iTime+1540.0;
    float r2 = float(x*x*t + y*y*t);
    int a = int(floor(r2*pow(16.0, 6.0-ceil(log2(r2)/4.0))));   
    fragColor = vec4(
        float((a >> 16) & 255) / 255.0,
        float((a >> 8) & 255) / 255.0,
        float((a >> 8) & 255) / 255.0,
        1.0
    );
}
