float hex = .866;         //sqrt(3.) / 2.;

vec2 map(vec2 p) {
    vec2 W, h;

    p.y /= hex;

        W = vec2(round(p.y / 2.), 0);
        h = round((p + W) / 2.) * 2. - W;

        dot(abs(p - h), vec2(2, 3)) > 4. 
            ? h += sign(p - h) * vec2(1, 2)
            : h;

    h.y *= hex;
    return h;
}

float hexDist(vec2 p) {
    p = abs(p);
    return max(dot(p, vec2(.5, hex)), p.x) - .94;
}

float render(vec2 p){
    return max(
               -length(p - map(p - .25 * vec2(3, 2) * vec2(cos(iTime*3.), sin(iTime*2.)))  *  .92) + .92, 
               hexDist(p - map(p))
           );
}

void mainImage(out vec4 o, vec2 u) {
    vec2 R = iResolution.xy,
         p = 16. * (u - .5 * R) / R.y;

    o = (1. + smoothstep(30. / R.y, .0, render(p))) 
                      * vec4(8, 12, 8, 0) / 17.;
}
