#define normal(p) normalize(map(p)-vec3(map(p-e.xyy),map(p-e.yxy),map(p-e.yyx)))
#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime

// @shane
vec4 tex3D(sampler2D tex, vec3 p, vec3 n) {
    n = max(n * n - .2, .001);
    n /= dot(n, vec3(1));
    vec3 tx = texture(tex, p.zy).xyz,
         ty = texture(tex, p.xz).xyz,
         tz = texture(tex, p.xy).xyz;
    return (mat3(tx * tx, ty * ty, tz * tz) * n).xyzz;
}

// @shane
float D_GGX(vec3 n, vec3 v, vec3 l, float roughness) {
    vec3 h = normalize(v + l);
    float NoH = clamp(dot(n, h), .001, 1.);
    float alpha = pow(roughness, 4.);
    float b = (NoH * NoH * (alpha - 1.) + 1.);
    return alpha /(3.14 * b * b);
}


// @shane
float G_Smith(vec3 n, vec3 v, float NoL, float roughness) {
    float k = .12 * roughness * roughness +  .25 * roughness + .12;

    float NoV = clamp(dot(n, v), .001, 1.); 
    float gl = NoL / (NoL + (1. - NoL) * k);
    float gv = NoV / (NoV * (1. - k) + k);
    return gl * gv / (4. * NoV);
}


// @Kamoshika
float map(vec3 p){
    float a = .5, s = 9., e;
    p = mod(p, 6.) - 3.;
    while((a += a) < t * .5 + 1.)
        e = length(p.zy) - 1.,
        s = min(s, e / a),
        p = abs(p),
        p = p.z > p.x ? p.zyx : p,
        p = p.y > p.x ? p.yxz : p,
        p.x -= 5.,
        p *= 2.;
    return s;
}



float softShadow(vec3 ro, vec3 lp, vec3 n, float k) {
    ro += n * .0015;
    vec3 D = lp - ro;
    float i, s, d, shade = 1.,
          end = max(length(D), .0001);

    D /= end;
    while(i++ < 48. && d < end) 
        s = map(ro + d * D),
        shade = min(shade, k * s / d),
        d += clamp(s * .9, .02, .25);

    return max(shade, 0.);
}


void mainImage(out vec4 o, vec2 u) {    
    vec2  r = iResolution.xy;
          u = (u - r.xy / 2.) / r.y;
          o *= 0.;

    float i, d, far = 30., s = 1.;

    vec3 p = vec3(0, 0, 1) * t, q,
         L = p + vec3(cos(t * .8) * 3., 0, 0),
         D = normalize(vec3(u, 2));

    D.zx *= rot(sin(t * .2));
    D.zy *= rot(cos(t * .3));

    while(i++ < 99. && s > 1e-4 && d < far) 
        s = map(p),
        d += s,
        p += s * D;

    vec3 N, ld, 
         e = vec3(.001, 0, 0);

    if(d < far) {
        N = normal(p), 
        ld = L - p;

        float shad  = softShadow(p, L, N, 8.),
              lDist = max(length(ld), .0001),
              atten = 30. / (30. + lDist * lDist),
              ambience = smoothstep(- 1., 1., N.y) * .5,
              BRDF = clamp(dot(N, ld / lDist), .001, 1.);

        // feel free to fork with better values
        o = .4 * vec4(9, 6, 5, 0) * tex3D(iChannel0, p * .25, N);
        BRDF = G_Smith(N, -D, BRDF, 2.) * 3.14;

        if(p.y > -1.3)
            o = .6 * vec4(8, 7, 6, 0) * tex3D(iChannel0, p * .2, N),
            BRDF = D_GGX(N, -D, ld / lDist, .6) * 2.;

        o *= (ambience + BRDF * shad) * atten;
    }

    o = pow(o, vec4(.6));
}
