mat2 rotate2d(float angle)
{
    return mat2(cos(angle), - sin(angle), sin(angle), cos(angle));
}

float verticalLine(in vec2 uv)
{
    if (uv.y > 0.0 && length(uv) < 1.)
    {
        float theta = mod(180.0 * atan(uv.y, uv.x)/3.14, 360.0);
        float gradient = clamp(1.0-theta/90.0,0.0,1.0);
        return 0.5 * gradient;
    }
    return 0.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.1415926;
    vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / min(iResolution.x,iResolution.y);
    vec3 color = vec3(0.24,0.52,0.86);

    mat2 rotation_matrix = rotate2d(-iTime*PI*2.0);

    vec3 col = mix(vec3(0.1), color, verticalLine(rotation_matrix * uv));

    fragColor = vec4(col,1.);
}
