#define I iResolution.xy
#define h(u) fract(dot(u.yxx * .1234, ceil(u.yxx) + 12.34) * 123.456)
#define R round

void mainImage( out vec4 U, in vec2 V )
{
    vec2 u = ( V + V - I.xy ) / min(I.x, I.y),
         t = R(u / -.2);

    float r = R( h( abs( t -  R( fract( iTime * .25 ) * 10. ) ) ) ), p;

    u -= .2 * R( u / .2 );

    if( r > 0. )
      u.y *= -1.;

    if( abs(u.x + u.y) < .15 )
      u += ( u.x + u.y > 0. ) ? -.1 : .1;

    // p = length(u) - .1;

    p = step( p, 0. );

    if( r == mod( t.x + t.y, 2. ) )
        p = 1. - p;

    U = vec4(vec3(p),1.);
    // p = ( r != mod( t.x + t.y, 2. ) ) ? p : -p; // under sub march ( step( gra, 0. ) )
}
