#define cor(p) (fract(2323.1 * sin(vec4(2, 4, 6, 0) + 11.1 * dot(p, p.yx + 1.))) * .5 + .5)
#define h11(a) fract(sin(a) * 437518.56453) + .1
#define pi acos(-1.)
#define t iTime

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;

    u = (u + u - r) / r.y;
    u = mix(
            vec2(
                atan(u.x, u.y) * 16. / 3.14,
                log(length(u) / mix(1., dot(u, u),  smoothstep(-.75, .75, sin(t * .3))))
            ),
            u *= vec2(30, 5),
            cos(t * .1) * .5 + .5
        );

    float i, j, hh,
          BD = 1.2, SY = 5., TS = .1,
          tt = iTime * TS * SY, 
          f = fract(tt);

      i = floor(u.x);
    u.x = fract(u.x);

    if(u.y + f / SY - BD < 0.) {
        u.y = u.y * SY + tt;
        j = floor(u.y);
        u.y = fract(u.y);
    } 

    else {
        u.y += f / SY - BD;
        j = floor(
                floor(u.y) - f
                   + BD * SY + tt);

        hh = h11(i + h11(j));
        u.y = fract(u.y) * SY
                  + min(0., (hh * f * SY / TS - SY));
    }

    o = cor(vec2(i, j))
        * smoothstep(20. / r.y, 0., length(u - .5) - .49)
        / length(u + .4);
}
