const float speed = 0.3;
const float tau = 2.0 * acos(-1.0);


const vec2 pts[3] = vec2[](
    vec2(0.2, 0.25),
    vec2(-0.2, 0.15),
    vec2(0., -0.3)
);
const vec2 ctr = vec2(0., 0.);
const vec3 blue = vec3(0., 0.4, 1.);
const vec3 orange = vec3(1., 0.6, 0.);
const vec3 black = vec3(0., 0., 0.);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 ctr = vec2(0.04 * cos(speed * tau * iTime), 0.04 * sin(speed * tau * iTime));
    vec2 fragNorm = fragCoord / iResolution.yy * 2. - vec2(iResolution.x/iResolution.y, 1.);
    vec2 ctr2frag = fragNorm - ctr;
    float d = -1./0.;
    vec3 col;
    bool nearPoint = length(ctr2frag) < 0.02;

    for (int i = 0; i < 3; i++) {
        vec2 ctr2pt = pts[i] - ctr;
        nearPoint = nearPoint || length(fragNorm - pts[i]) < 0.02 ;
        d = max(d, dot(ctr2frag, ctr2pt)/(length(ctr2pt)*length(ctr2pt)));
    }
    d = d - 1.;

    col = nearPoint ? black
        : d < -0.02 ? blue
        : d > 0.02 ? orange
        : black;

    fragColor = vec4(col, 1.0);
}
