vec2 CenterUV(vec2 fragCoord )//https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}
float Remap(float In, vec2 InMinMax, vec2 OutMinMax)
{
    return OutMinMax.x + (In - InMinMax.x) * (OutMinMax.y - OutMinMax.x) / (InMinMax.y - InMinMax.x);
}
vec2 RotateUV(vec2 uv,float angle)//uv旋转
{
    return vec2(uv.x * cos(angle) - uv.y * sin(angle),uv.x * sin(angle) + uv.y *cos(angle));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CenterUV(fragCoord );
    float sds = 0.;
    for(int i=0;i<50;i++){
        float t = mod(iTime*0.2,.1);
        float r = (t + float(i)*0.1)*0.3;
        float angle = r * 10.;
        vec2 RotateUV = RotateUV(uv,angle);
        float sd = length(RotateUV+r)-Remap(max(0.,r),vec2(0.,1.5),vec2(0.1,0.4));
        sds += (max(0.,(1.-pow(sd*2.,0.2)))+ step(sd,0.01))*0.15;
    }

    vec3 color = mix(vec3(0.278,0.780,0.824),vec3(0.38,0.678,0.937),length(uv));
    color = mix(color,vec3(1.),sds);
    fragColor = vec4(color,1.0);
}
